package com.fee.example.one.controller;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;@Controller@RequestMapping("/hello")public class HelloController {	@RequestMapping(method = RequestMethod.GET)	public String showHello(ModelMap model) {		model.addAttribute("message", "Hello Spring MVC Framework!");		return "hello";	}	@RequestMapping(value = "/sayHi", method = RequestMethod.GET)	public String sayHi(ModelMap model) {		model.addAttribute("message", "Hello world");		return "hello";	}}