package com.fee.example.one.controller;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;@Controllerpublic class IndexController {	@RequestMapping(value = "/", method = RequestMethod.GET)	public String index() {		return "index";	}	@RequestMapping(value = "/redirect", method = RequestMethod.GET)	public String redirect() {		return "redirect:final";	}	@RequestMapping(value = "/final", method = RequestMethod.GET)	public String finalPage() {		return "index-final";	}	@RequestMapping(value = "/htmlPage", method = RequestMethod.GET)	public String htmlPage() {		return "redirect:/html/final.html";	}}