package com.fee.example.one.controller;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.servlet.ModelAndView;import com.fee.example.exception.SpringException;import com.fee.example.one.bean.Student;@Controllerpublic class StudentController {	@RequestMapping(value = "/student", method = RequestMethod.GET)	public ModelAndView Student() {		return new ModelAndView("student", "command", new Student());	}	@RequestMapping(value = "/addStudent", method = RequestMethod.POST)	@ExceptionHandler({ SpringException.class })	public String addStudent(@ModelAttribute("SpringWeb") Student student,			ModelMap model) {		if (student.getName().length() < 6) {			throw new SpringException("Given name is too short");		} else {			model.addAttribute("name", student.getName());		}		model.addAttribute("age", student.getAge());		model.addAttribute("id", student.getId());		return "student-result";	}}