package com.fee.example.two.controller;import java.util.ArrayList;import java.util.List;import javax.validation.Valid;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.validation.BindingResult;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import com.fee.example.two.bean.CollegeStudent;@Controller@RequestMapping("/college")public class CollegeStudentController {	@RequestMapping(method = RequestMethod.GET)	public String register(ModelMap model) {		CollegeStudent student = new CollegeStudent();		model.addAttribute("collegestudent", student);		return "register";	}	@RequestMapping(method = RequestMethod.POST)	public String create(@Valid CollegeStudent collegestudent,			BindingResult result, ModelMap model) {		if (result.hasErrors()) {			return "register";		}		model.addAttribute("success", "Dear " + collegestudent.getFirstName()				+ ", you registration completed successfully");		return "success";	}	@ModelAttribute("sections")	public List<String> initalizeSection() {		List<String> sections = new ArrayList<String>(10);		sections.add("Graduate");		sections.add("Post Graduate");		sections.add("Research");		return sections;	}	@ModelAttribute("countries")	public List<String> initalizeCountry() {		List<String> country = new ArrayList<String>(10);		country.add("USA");		country.add("CANADA");		country.add("FRANCE");		country.add("GERMANY");		country.add("ITALY");		country.add("OTHER");		return country;	}	@ModelAttribute("subjects")	public List<String> initalizeSubjects() {		List<String> subject = new ArrayList<String>(10);		subject.add("Physics");		subject.add("Chemistry");		subject.add("Life Science");		subject.add("Computer");		subject.add("Math");		return subject;	}}