package com.fee.example.two.controller;import java.util.HashMap;import java.util.Map;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.servlet.ModelAndView;@RestController@RequestMapping(value = "/rest")public class RestDemoController {	@RequestMapping(value = "/sayHello/{username}", method = RequestMethod.GET)	public ModelAndView sayHello(@PathVariable String username) {		Map<String, Object> map = new HashMap<String, Object>();		map.put("message", "Hello " + username);		return new ModelAndView("hello", map);	}}