package com.fee.example.two.dao;import java.util.ArrayList;import java.util.List;import org.springframework.stereotype.Repository;import com.fee.example.two.bean.Employee;@Repositorypublic class EmployeeDAOImpl implements EmployeeDAO {	@Override	public List<Employee> getAllEmployees() {		List<Employee> employees = new ArrayList<Employee>();		Employee employee1 = new Employee();		employee1.setId(1);		employee1.setFirstName("xia");		employee1.setLastName("tian");		employees.add(employee1);		Employee employee2 = new Employee();		employee2.setId(2);		employee2.setFirstName("liu");		employee2.setLastName("hao");		employees.add(employee2);		return employees;	}}