package com.nova.algorithms;import java.util.HashMap;public class TwoSum {	public static void main(String[] args) {		int i = -1;		int j = 1;		System.out.println(i + j);	}	public int[] twoSum(int[] numbers, int target) {		int len = numbers.length;		HashMap<Integer, Integer> map = new HashMap<Integer, Integer>(len);		for (int i = 0; i < len; i++) {			map.put(i, numbers[i]);		}		int h = 0;		int k = 0;		for (int i = 0; i < len; i++) {			int diff = target - numbers[i];			if (map.containsValue(diff)) {				h = i;				k = diff;			}		}		for (int i = 0; i < len; i++) {			if (k == numbers[i]) {				k = i;				break;			}		}		return new int[] { h + 1, k + 1 };	}}