package com.nova.demo.action;import java.util.Map;import com.nova.demo.model.Book;import com.opensymphony.xwork2.ActionSupport;public class BookAction extends ActionSupport {	private static final long serialVersionUID = 1L;	private Integer id;	private Map<Integer, Book> map;	private Book book;	public String index() {		System.out.println("welcome to book system");		book = new Book();		map = book.getAllBooks();		return "index";	}	public String show() {		System.out.println("show id=" + id);		book = new Book();		book = book.getAllBooks().get(id);		return "show";	}	public String update() {		System.out.println("update");		Book b = new Book();		map = b.getAllBooks();		map.put(book.getId(), book);		return "index";	}	public String create() {		Book b = new Book();		map = b.getAllBooks();		book.setId(b.getKey());		map.put(book.getId(), book);		return "index";	}	public String destroy() {		Book b = new Book();		map = b.getAllBooks();		map.remove(id);		return "index";	}	public String editNew() {		return "create";	}	public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public Map<Integer, Book> getMap() {		return map;	}	public void setMap(Map<Integer, Book> map) {		this.map = map;	}	public Book getBook() {		return book;	}	public void setBook(Book book) {		this.book = book;	}}