package com.nova.demo.action;import java.io.File;import java.io.IOException;import org.apache.commons.io.FileUtils;import org.apache.struts2.ServletActionContext;import org.apache.struts2.convention.annotation.Action;import org.apache.struts2.convention.annotation.ParentPackage;import org.apache.struts2.convention.annotation.Result;import org.apache.struts2.convention.annotation.Results;import com.opensymphony.xwork2.ActionSupport;@ParentPackage("json-default")@Action("upload")@Results({ @Result(name = "success", type = "json", params = { "root",		"fileFileName" }) })public class UploadAction extends ActionSupport {	private static final long serialVersionUID = 1L;	private File file;	private String fileFileName;	private String fileContentType;	private boolean result;	public String upload() {		String realPath = ServletActionContext.getServletContext().getRealPath(				"file/");		File path = new File(realPath);		System.out.println(fileFileName);		try {			FileUtils.copyFile(file, new File(path, fileFileName));		} catch (IOException e) {			e.printStackTrace();		}		result = true;		return "success";	}	public File getFile() {		return file;	}	public void setFile(File file) {		this.file = file;	}	public String getFileFileName() {		return fileFileName;	}	public void setFileFileName(String fileFileName) {		this.fileFileName = fileFileName;	}	public String getFileContentType() {		return fileContentType;	}	public void setFileContentType(String fileContentType) {		this.fileContentType = fileContentType;	}	public boolean isResult() {		return result;	}	public void setResult(boolean result) {		this.result = result;	}}