package com.nova.demo.model;import java.util.HashMap;import java.util.Map;public class Book {	private Integer id;	private String name;	private double price;	private String author;	public static Map<Integer, Book> map = new HashMap<Integer, Book>();	public static Integer i = 4;	public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public double getPrice() {		return price;	}	public void setPrice(double price) {		this.price = price;	}	public String getAuthor() {		return author;	}	public void setAuthor(String author) {		this.author = author;	}	public Map<Integer, Book> getAllBooks() {		return map;	}	public Integer getKey() {		return ++i;	}	static {		Book book = new Book();		book.setId(1);		book.setName("小学数学");		book.setPrice(30.0);		book.setAuthor("张学军");		map.put(book.getId(), book);		book = new Book();		book.setId(2);		book.setName("小学语文");		book.setPrice(34.0);		book.setAuthor("张花花");		map.put(book.getId(), book);		book = new Book();		book.setId(3);		book.setName("思想品德");		book.setPrice(21.0);		book.setAuthor("吴品");		map.put(book.getId(), book);		book = new Book();		book.setId(4);		book.setName("自然");		book.setPrice(23.0);		book.setAuthor("刘自然");		map.put(book.getId(), book);	}}