package com.nova.study.thread;import java.util.concurrent.CountDownLatch;public class CountThread implements Runnable {	private CountDownLatch threadsSignal;	public CountThread(CountDownLatch threadsSignal) {		this.threadsSignal = threadsSignal;	}	@Override	public void run() {		System.out.println(Thread.currentThread().getName() + "-start...");		System.out.println("start thread..." + threadsSignal.getCount());		threadsSignal.countDown();		System.out.println(Thread.currentThread().getName() + "-end,surplus "				+ threadsSignal.getCount());	}}