package com.nova.study.thread;import java.util.concurrent.CountDownLatch;import java.util.concurrent.Executor;import java.util.concurrent.Executors;public class MultiThread {	public void testThread() throws InterruptedException {		int threadNum = 10;		CountDownLatch threadSignal = new CountDownLatch(threadNum);		Executor executor = Executors.newFixedThreadPool(threadNum);		for (int i = 0; i < threadNum; i++) {			Runnable task = new CountThread(threadSignal);			executor.execute(task);		}		threadSignal.await();		System.out.println(Thread.currentThread().getName() + "-end");	}	public static void main(String[] args) throws InterruptedException {		MultiThread mt = new MultiThread();		mt.testThread();	}}